import { Deserializer, Serializer } from '@cedra-labs/ts-sdk';
import { CedraSignInInput } from '@cedra-labs/wallet-standard';

export function serializeCedraSignInInput(serializer: Serializer, value: CedraSignInInput) {
  serializer.serializeStr(JSON.stringify(value));
}

export function deserializeCedraSignInInput(deserializer: Deserializer): CedraSignInInput {
  return JSON.parse(deserializer.deserializeStr());
}
