import { registerWallet } from "@cedra-labs/wallet-standard";
import { CedraConnectWalletConfig } from "./CedraConnectWallet";
import { CedraConnectGoogleWallet } from "./CedraConnectGoogleWallet";
import { CedraConnectAppleWallet } from "./CedraConnectAppleWallet";
export * from "./CedraConnectAccount";
export * from "./CedraConnectWallet";
export { CedraConnectGoogleWallet, CedraConnectAppleWallet };

export function registerCedraConnect(config: CedraConnectWalletConfig = {}) {
  const googleWalletPlugin = new CedraConnectGoogleWallet(config);
  const appleWalletPlugin = new CedraConnectAppleWallet(config);

  registerWallet(googleWalletPlugin);
  registerWallet(appleWalletPlugin);
}
